
public class Count1to50Divisibleby5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//count number between 1 to 50 divisible by 5//
		int i;
		for(i=1;i<=50;i++)
		{
			if(i%5==0)
			{
				System.out.println("Divisible by 5:"+i);
			}
		}

	}

}
