
public class SumofallevenNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//write the code of sum of all even number 1 to n//
		int i;
		int num=100,sum=0;
		for(i=2;i<=100;i++)
		{
			if(i%2==0)
			{
				sum+=i;
				System.out.println(+sum);
			}
				
		}

	}

}
